import { CommonModule } from '@angular/common';
import { Component, effect, inject } from '@angular/core';
import { MatSliderModule } from '@angular/material/slider';
import { ProductStore } from './product-store.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css'],
  standalone: true,
  imports: [CommonModule, MatSliderModule],
})
export class ProductComponent {
  private store = inject(ProductStore);
  minPrice = this.store.minPrice;
  maxPrice = this.store.maxPrice;
  products = this.store.products;

  constructor() {
    effect(() => {
      console.log('load');
      this.store.load();
    });
  }

  updateRange(minPrice: number, maxPrice: number): void {
    this.store.updateRange(minPrice, maxPrice);
  }
}
<div class="container">
  <h1>Product Search</h1>
  <label>Price Range: </label>
  <mat-slider min="0" max="1500" discrete>
    <input
      matSliderStartThumb
      [value]="minPrice()"
      (valueChange)="updateRange($event, maxPrice())"
    />
    <input
      matSliderEndThumb
      [value]="maxPrice()"
      (valueChange)="updateRange(minPrice(), $event)"
    />
  </mat-slider>

  <div class="products">
    <div *ngFor="let p of products()">
      <div>{{ p.name }}: {{ p.price }}$</div>
    </div>
  </div>
</div>
